import * as React from "react";
import { PageParamsProvider as PageParamsProvider__ } from "@plasmicapp/react-web/lib/host";
import GlobalContextsProvider from "../components/plasmic/ez_marketing_platform/PlasmicGlobalContextsProvider";

import { PlasmicCEditProfile } from "../components/plasmic/ez_marketing_platform/PlasmicCEditProfile";
import { useRouter } from "next/router";

import { getSupabaseC } from "../lib/c-supabaseClient";

function CEditProfile() {
  const router = useRouter();
  const [ready, setReady] = React.useState(false);
  const [userId, setUserId] = React.useState<string | null>(null);

  React.useEffect(() => {
    const supabase = getSupabaseC();

    supabase.auth.getSession().then(({ data }) => {
      const session = data.session;

      // üö´ SEM LOGIN ‚Üí BLOQUEIA
      if (!session) {
        router.replace("/c-login");
        return;
      }

      // ‚úÖ USER ID (FONTE DA VERDADE)
      const uid = session.user?.id;
      if (!uid) {
        router.replace("/c-login");
        return;
      }

      // ‚úÖ TOKEN
      const token = session.access_token;

      if (typeof window !== "undefined" && typeof localStorage !== "undefined") {
        if (token && token.split(".").length === 3) {
          localStorage.setItem("sb-company-access-token", token);
        } else {
          router.replace("/c-login");
          return;
        }
      }

      setUserId(uid);
      setReady(true);
    });
  }, [router]);

  // ‚è≥ N√ÉO RENDERIZA NADA ANTES DA VALIDA√á√ÉO
  if (!ready || !userId) return null;

  return (
    <GlobalContextsProvider>
      <PageParamsProvider__
        route={router.pathname}
        params={router.query}
        query={router.query}
      >
        {/* ‚úÖ userId PASSADO PARA O PLASMIC */}
        <PlasmicCEditProfile userId={userId} />
      </PageParamsProvider__>
    </GlobalContextsProvider>
  );
}

export default CEditProfile;
